package StringQuestions;

public class FlyodTraingle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int noOfLines=4,k=1;
		for(int i=1;i<=noOfLines;i++)
		{
			for(int j=1;j<=i;j++,k++)
			{
				System.out.print(k + " ");
			}
			System.out.println();
		}
		
		

	}

}
